-- Create "bookmark" table
CREATE TABLE "public"."bookmark" ("id" bigserial NOT NULL, "link" character varying NULL, "category_id" bigint NULL, "workspace_id" bigint NULL, "summary" character varying NULL, "created_at" timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, "updated_at" timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY ("id"), CONSTRAINT "bookmark_category_id_fkey" FOREIGN KEY ("category_id") REFERENCES "public"."category" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "bookmark_workspace_id_fkey" FOREIGN KEY ("workspace_id") REFERENCES "public"."workspace" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "comment" table
CREATE TABLE "public"."comment" ("id" bigserial NOT NULL, "bookmark_id" bigint NULL, "user_id" bigint NULL, "context" bigint NULL, "created_at" timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, "updated_at" timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY ("id"), CONSTRAINT "comment_bookmark_id_fkey" FOREIGN KEY ("bookmark_id") REFERENCES "public"."bookmark" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "comment_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "public"."user" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
